INSERT INTO ST_TREE_NODE (tree_key,tree_node_level,single_condition,tree_node_field_type,tree_node_field,tree_node_sql,tree_node_remark) 
	VALUES ('st_office_tree', 1, 1, 'field', 'office_id', 'SELECT office_id AS node_value, office_shortname AS node_text, office_name AS node_title, ISNULL(sub_nodes,0) AS sub_nodes, office_id AS officeId
FROM ST_OFFICE t LEFT JOIN (SELECT poffice_id, COUNT(*) AS sub_nodes FROM ST_OFFICE WHERE poffice_id = ''%officeId%'' AND del_tag = 0 GROUP BY poffice_id) s 
ON t.office_id = s.poffice_id  
WHERE t.office_id = ''%officeId%'' AND t.del_tag = 0
ORDER BY office_order', '');
INSERT INTO ST_TREE_NODE (tree_key,tree_node_level,single_condition,tree_node_field_type,tree_node_field,tree_node_sql,tree_node_remark) 
	VALUES ('st_office_tree', 2, 1, 'field', 'office_id', 'SELECT t.office_id AS node_value, office_shortname AS node_text, office_name AS node_title, CASE WHEN c.children IS NULL THEN 0 ELSE c.children END AS sub_nodes, t.office_id AS officeId
FROM ST_OFFICE t LEFT JOIN (SELECT t2.office_id, COUNT(*) AS children FROM ST_OFFICE t2 INNER JOIN ST_OFFICE t3 ON t2.office_id = t3.Poffice_id WHERE t2.poffice_id = ''%i%'' AND t3.del_tag = 0 GROUP BY t2.office_id) c ON t.office_id = c.office_id WHERE t.poffice_id = ''%i%'' AND del_tag = 0
ORDER BY office_order', '');
